      //     <div className="popup-image__content-container">
      //       <figure className="popup-image__photo">
      //         <img src="" alt="" className="popup-image__preview" />
      //         <figcaption className="popup-image__title"></figcaption>
      //       </figure>
      //     </div>
      //   </div>
      // </div>
  );
}

export default App;
